mapper.select:
SELECT post_title, post_location_keyword, post_location_lat, post_location_lng, post_begin_date, post_end_date, post_content, post_register_date, post_update_date, space_id, user_id
FROM posts
WHERE post_id=#{postId}

SELECT DISTINCT(p.post_id) post_id, post_title, post_location_keyword, post_register_date, post_update_date, post_location_lat, post_location_lng, space_id FROM posts p LEFT JOIN post_tags t ON p.post_id=t.post_id WHERE p.space_id = #{spaceId}
AND post_title LIKE CONCAT('%',#{keyword},'%')
AND #{date} BETWEEN post_begin_date AND post_end_date
OR #{date} BETWEEN post_begin_date AND post_end_date
AND t.tag_id IN
#{tag}
AND user_id IN
#{writer}
ORDER BY post_register_date DESC LIMIT #{startIndex}, #{amount}

SELECT COUNT(x.post_id) FROM ( SELECT DISTINCT(p.post_id) post_id, space_id FROM posts p LEFT JOIN post_tags t ON p.post_id=t.post_id WHERE p.space_id = #{spaceId}
AND post_title LIKE CONCAT('%',#{keyword},'%')
AND #{date} BETWEEN post_begin_date AND post_end_date
OR #{date} BETWEEN post_begin_date AND post_end_date
AND t.tag_id IN
#{tag}
AND user_id IN
#{writer}
) x

SELECT space_id, post_id, post_title, post_register_date
FROM posts
WHERE user_id = #{userId}
ORDER BY post_register_date DESC, post_id DESC
LIMIT #{startIndex}, 10;
